import {useEffect, useState} from "react";
import { DataGrid, GridRowsProp, GridColDef } from '@mui/x-data-grid';

// ACTION
import * as StockAction from '../action/StockAction'

export default function MainContainer() {
    const [themeStockList, setThemeStockList] = useState([])

    const arr = []

    useEffect(() => {
        getStockRecommendList()
    }, [])

    async function getStockRecommendList() {
        const list = await StockAction.getStockRecommendList()
        await setThemeStockList()
    }

    async function setListArr(list) {
        const arr = []
        for(let obj in list) {
            arr.push({
                id: obj.themeStockUid,
                theme: obj.theme,
                themesInfo: obj.themesInfo
            })
        }
        return arr
    }

    const rows: GridRowsProp = arr

    const columns: GridColDef[] = [
        { field: 'theme', headerName: '테마', width: 150 },
        { field: 'themesInfo', headerName: '정보', width: 500 },
    ];


    return (
        <>
            <div>
                <DataGrid columns={columns} rows={rows} />
            </div>
        </>
    )
}
