import {useEffect, useState} from "react";
import * as StockAction from "../action/StockAction";
import {getPortfolioList} from "../action/StockAction";
import {Grid, TableBody, TableCell} from "@mui/material";
import CardComponent from "../component/CardComponent";

export default function PortfolioContainer() {
    const [portfolioList, setPortfolioList] = useState([])
    const [portfolioDetail, setPortfolioDetail] = useState([])
    useEffect(() => {
        getPortfolioList()
    }, [])

    async function getPortfolioList() {
        const list = await StockAction.getPortfolioList()
        await console.log(list)
        await setPortfolioList(list.list)
        await setPortfolioDetail(list.detail)
    }

    return (
        <>
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1 }}>
                { portfolioList && portfolioList.map((list, index) =>
                    <Grid item xs={6} key={index}>
                        <CardComponent title={portfolioDetail[index].portfolioName}
                                       content={ list && list.map((stock, subIndex) => (
                                           <TableBody>
                                               <TableCell>{stock.stockName}</TableCell>
                                               <TableCell>{stock.tradePrice}</TableCell>
                                               <TableCell>{stock.tradeTp}</TableCell>
                                               <TableCell>{stock.weight}</TableCell>
                                           </TableBody>
                                           ))
                                       } />
                    </Grid>
                )}
            </Grid>
        </>
    )
}
