import {useEffect, useState} from "react";
import { DataGrid, GridRowsProp, GridColDef } from '@mui/x-data-grid';

// ACTION
import * as StockAction from '../action/StockAction'
import {Grid} from "@mui/material";

export default function MainContainer() {
    const [themeStockList, setThemeStockList] = useState([])
    useEffect(() => {
        getStockRecommendList()
    }, [])

    async function getStockRecommendList() {
        const list = await StockAction.getStockRecommendList()
        await setThemeStockList(list)
    }

    const rows: GridRowsProp = setThemeStockList

    const columns: GridColDef[] = [
        { field: 'theme', headerName: '테마', width: 150 },
        { field: 'themesInfo', headerName: '정보', width: 500 },
    ];


    return (
        <>
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>

                    { themeStockList && themeStockList.map((list, index) =>
                        <Grid item xs={6}>
                            <Item>
                                {list.theme}
                                {list.themeInfo}
                            </Item>
                        </Grid>
                    )}

                <Grid item xs={6}>
                    <Item>2</Item>
                </Grid>
                <Grid item xs={6}>
                    <Item>3</Item>
                </Grid>
                <Grid item xs={6}>
                    <Item>4</Item>
                </Grid>
            </Grid>
            <div>
                <tr>
                    <th>테마</th>
                    <th>정보</th>
                </tr>
                { themeStockList && themeStockList.map((list, index) =>
                    <tr>
                        <td>{list.theme}</td>
                        <td>{list.themeInfo}</td>
                    </tr>
                )}
            </div>
        </>
    )
}
