import {useEffect, useState} from "react";
import { DataGrid, GridRowsProp, GridColDef } from '@mui/x-data-grid';

// ACTION
import * as StockAction from '../action/StockAction'

import { styled } from '@mui/material/styles';
import { Grid, Paper } from "@mui/material";

export default function MainContainer() {
    const [themeStockList, setThemeStockList] = useState([])
    useEffect(() => {
        getStockRecommendList()
    }, [])

    async function getStockRecommendList() {
        const list = await StockAction.getStockRecommendList()
        await setThemeStockList(list)
    }

    const Item = styled(Paper)(({ theme }) => ({
        backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
        ...theme.typography.body2,
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    }));

    return (
        <>
            <Grid container rowSpacing={1} columnSpacing={{ xs: 1 }}>
                    { themeStockList && themeStockList.map((list, index) =>
                        <Grid item xs={6}>

                                {list.theme}
                                {list.themeInfo}

                        </Grid>
                    )}

            </Grid>
            <div>
                <tr>
                    <th>테마</th>
                    <th>정보</th>
                </tr>
                { themeStockList && themeStockList.map((list, index) =>
                    <tr>
                        <td>{list.theme}</td>
                        <td>{list.themeInfo}</td>
                    </tr>
                )}
            </div>
        </>
    )
}
